package com.github.tinosteinort.flda.fixedlengthstring.reader;

import com.github.tinosteinort.flda.fixedlengthstring.FixedLengthString;
import com.github.tinosteinort.flda.fixedlengthstring.FixedLengthStringAttribute;
import org.junit.Test;

import java.math.BigDecimal;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNull;

public class BigDecimalAttributeReaderTest {

    private final BigDecimalAttributeReader reader = new BigDecimalAttributeReader();

    @Test public void bigValue() {
        final FixedLengthString data = new FixedLengthString(".9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999");
        final FixedLengthStringAttribute<BigDecimal> attribute = new FixedLengthStringAttribute<>(BigDecimal.class, 0, 579);

        assertEquals(new BigDecimal(".9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999"), reader.read(data, attribute));
    }

    @Test public void nullValue() {
        final FixedLengthString data = new FixedLengthString("     ");
        final FixedLengthStringAttribute<BigDecimal> attribute = new FixedLengthStringAttribute<>(BigDecimal.class, 0, 5);

        assertNull(reader.read(data, attribute));
    }
}
